import React from "react";
import Button from '@mui/material/Button';
import SendIcon from '@mui/icons-material/Send';
import Stack from '@mui/material/Stack';
import backgroundImage from '../images/careerbgimg.jpg'; // Import your background image file
import backgroundimgforjobfind from "../images/Careerpgbgimg2.jpg"

const Career = () => {
  
  const redirectToLinkedInJobs = () => {
    window.open('https://www.linkedin.com/company/techtorch-solutions-pune/jobs/', '_blank');
  };
  
  return (
    <div style={styles.container}>
      <h1>Career</h1>
      <div>
        The Future is open.“Your work is going to fill a large part of your
        life, the only way to do great work is to love what you do”, your Life
        with Tech Torch Solutions Pvt. Ltd. Tech Torch build job opportunities
        for the passionate one.
      </div>
      <Stack direction="row" spacing={2} style={styles.buttonContainer}>
        <Button variant="contained" endIcon={<SendIcon />} style={styles.button} onClick={redirectToLinkedInJobs}>
          View Current Openings
        </Button>
      </Stack>
      <div style={styles.contactContainer}>
        <div style={styles.contactContent}>
          <div>Couldn't find the job you are looking for?</div>
          <div>Send your resume on </div>
          <div>hr@techtorchsolutions.com</div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    backgroundImage: `url(${backgroundImage})`, // Apply background image
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh', // Ensure full screen height
    position: 'relative', // Necessary for absolute positioning of contact container
    padding: '100px',
    color: 'black', 
  },
  buttonContainer: {
    marginTop: '60px', // Adjust margin top as needed
    justifyContent: 'center',
    marginBottom: '60px'
  },
  button: {
    margin: 'auto', // Center horizontally
  },
  contactContainer: {
    position: 'absolute',
    top: '50%',
    left: '5%', // Adjust left position as needed
    transform: 'translateY(-50%)',
    width: '50%', // Adjust width of contact container as needed
  },
  contactContent: {
    background: `url(${backgroundimgforjobfind})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    padding: '20px',
    opacity: 0.6, // Adjust opacity of image as needed
    borderRadius: '10px',
    color: 'white',
  },
};

export default Career;
