import React, { useState, useEffect } from "react";
import "./Range.css";

const Range = () => {
  const [counts, setCounts] = useState({
    customers: 0,
    projects: 0,
    conversionRate: 0,
    workshops: 0
  });

  useEffect(() => {
    const intervals = Object.keys(counts).map(key => {
      const interval = setInterval(() => {
        setCounts(prevCounts => {
          if (prevCounts[key] < 15) {
            return { ...prevCounts, [key]: prevCounts[key] + 1 };
          } else {
            clearInterval(interval);
            return prevCounts;
          }
        });
      }, 100);
      return interval;
    });

    return () => {
      intervals.forEach(interval => clearInterval(interval));
    };
  }, [counts]);

  return (
    <div className="range-container">
      <div className="range-item">{counts.customers}+ Happy Customers</div>
      <div className="range-item">{counts.projects}+ Successful Projects</div>
      <div className="range-item">{counts.conversionRate}% Conversion Rate</div>
      <div className="range-item">{counts.workshops}+ Workshops Conducted</div>
    </div>
  );
};

export default Range;
